import express from 'express';
import cors from 'cors';
import dotenv from 'dotenv';
import helmet from 'helmet';
import rateLimit from 'express-rate-limit';

import authRoutes from './routes/auth';
import entityRoutes from './routes/entities';
import serviceRoutes from './routes/services';
import govAIRoutes from './routes/govAI';
import { errorHandler } from './middleware/errorHandler';

dotenv.config();

const app = express();

// 1. Security Middleware
app.use(helmet());

// Trust Vercel Proxy (Required for rate limiting and IP tracking)
app.set('trust proxy', 1);

// 2. CORS Configuration
// valid configuration: origin * cannot correspond with credentials true.
// Since we use Bearer tokens, we don't strictly need credentials:true unless using cookies.
const corsOptions = {
    origin: '*',
    credentials: false, // Changed to false to allow origin: '*'
    methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
    allowedHeaders: ['Content-Type', 'Authorization']
};
app.use(cors(corsOptions));

// Debug Route
app.get('/api/debug', (req, res) => {
    res.json({
        status: 'alive',
        time: new Date(),
        env: process.env.NODE_ENV,
        database_url_configured: !!process.env.DATABASE_URL
    });
});

// 3. Rate Limiting
const limiter = rateLimit({
    windowMs: 15 * 60 * 1000,
    max: 100,
    message: 'Too many requests from this IP, please try again later.'
});
app.use('/api/', limiter);

// 4. Body Parser (Limited)
app.use(express.json({ limit: '10kb' }));

// 5. Routes
app.use('/api/auth', authRoutes);
app.use('/api/entities', entityRoutes);
app.use('/api/services', serviceRoutes);
app.use('/api/gov-ai', govAIRoutes);

app.get('/health', (req, res) => {
    res.json({ status: 'ok', timestamp: new Date() });
});

// 6. Global Error Handler (Must be last)
app.use(errorHandler);

export default app;
